import React, { useState, useEffect } from 'react';
import { DragDropContext, DropResult } from 'react-beautiful-dnd';
import { AppData, TodoList, Task } from './types';
import ListSelector from './components/ListSelector';
import TaskList from './components/TaskList';
import AddListButton from './components/AddListButton';

function App() {
  const [data, setData] = useState<AppData>({ lists: [] });
  const [activeListId, setActiveListId] = useState<string>('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('/api/data');
      const jsonData = await response.json();
      setData(jsonData);
      setActiveListId(jsonData.lists[0]?.id || '');
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch data:', error);
      setLoading(false);
    }
  };

  const saveData = async (newData: AppData) => {
    try {
      await fetch('/api/data', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newData),
      });
    } catch (error) {
      console.error('Failed to save data:', error);
    }
  };

  const handleDragEnd = (result: DropResult) => {
    if (!result.destination) return;

    const activeList = data.lists.find(list => list.id === activeListId);
    if (!activeList) return;

    const newTasks = Array.from(activeList.tasks);
    const [reorderedTask] = newTasks.splice(result.source.index, 1);
    newTasks.splice(result.destination.index, 0, reorderedTask);

    const updatedTasks = newTasks.map((task, index) => ({
      ...task,
      order: index,
    }));

    const newData = {
      ...data,
      lists: data.lists.map(list =>
        list.id === activeListId
          ? { ...list, tasks: updatedTasks }
          : list
      ),
    };

    setData(newData);
    saveData(newData);
  };

  const activeList = data.lists.find(list => list.id === activeListId);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-dodash-purple"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-dark text-white">
      <header className="py-6">
        <div className="container mx-auto px-4">
          <div className="flex items-center justify-center mb-8">
            <img src="/logo.png" alt="DoDash" className="h-12" />
          </div>
          <div className="flex items-center justify-center space-x-4">
            <ListSelector
              lists={data.lists}
              activeListId={activeListId}
              onSelectList={setActiveListId}
              onUpdateList={(updatedList) => {
                const newData = {
                  ...data,
                  lists: data.lists.map(list =>
                    list.id === updatedList.id ? updatedList : list
                  ),
                };
                setData(newData);
                saveData(newData);
              }}
              onDeleteList={(listId: string) => {
                const newData = {
                  ...data,
                  lists: data.lists.filter(list => list.id !== listId),
                };
                setData(newData);
                saveData(newData);
                setActiveListId(newData.lists[0]?.id || '');
              }}
            />
            <AddListButton
              onAdd={(newList: TodoList) => {
                const newData = {
                  ...data,
                  lists: [...data.lists, newList],
                };
                setData(newData);
                saveData(newData);
                setActiveListId(newList.id);
              }}
            />
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        {activeList && (
          <DragDropContext onDragEnd={handleDragEnd}>
            <TaskList
              list={activeList}
              onUpdateList={(updatedList) => {
                const newData = {
                  ...data,
                  lists: data.lists.map(list =>
                    list.id === updatedList.id ? updatedList : list
                  ),
                };
                setData(newData);
                saveData(newData);
              }}
            />
          </DragDropContext>
        )}
      </main>
    </div>
  );
}

export default App;
