import React, { useState } from 'react';
import { TodoList } from '../types';
import { HexColorPicker } from 'react-colorful';

interface ListSelectorProps {
  lists: TodoList[];
  activeListId: string;
  onSelectList: (id: string) => void;
  onUpdateList: (list: TodoList) => void;
  onDeleteList: (id: string) => void;
}

const ListSelector: React.FC<ListSelectorProps> = ({
  lists,
  activeListId,
  onSelectList,
  onUpdateList,
  onDeleteList,
}) => {
  const [editingListId, setEditingListId] = useState<string | null>(null);
  const [editingName, setEditingName] = useState('');
  const activeList = lists.find(list => list.id === activeListId);

  const handleEdit = (list: TodoList) => {
    setEditingListId(list.id);
    setEditingName(list.name);
  };

  const handleSave = (list: TodoList) => {
    if (editingName.trim()) {
      onUpdateList({ ...list, name: editingName.trim() });
    }
    setEditingListId(null);
  };

  return (
    <div className="flex flex-col items-center space-y-6">
      <div className="flex items-center justify-center space-x-4">
        <div className="flex flex-wrap justify-center gap-3">
          {lists.map((list) => (
            <div key={list.id} className="relative">
              <button
                className={`px-6 py-2.5 rounded-full transition-all duration-200 flex items-center space-x-3 shadow-lg hover:shadow-xl ${
                  list.id === activeListId
                    ? 'bg-opacity-100 scale-105'
                    : 'bg-opacity-20 hover:bg-opacity-40 hover:scale-102'
                }`}
                style={{ backgroundColor: list.color }}
                onClick={() => onSelectList(list.id)}
              >
                {editingListId === list.id ? (
                  <input
                    type="text"
                    value={editingName}
                    onChange={(e) => setEditingName(e.target.value)}
                    onBlur={() => handleSave(list)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSave(list)}
                    className="bg-transparent border-none outline-none text-white placeholder-white text-lg font-medium"
                    autoFocus
                  />
                ) : (
                  <>
                    <span className="text-lg font-medium">{list.name}</span>
                    <div className="flex space-x-2">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          handleEdit(list);
                        }}
                        className="opacity-60 hover:opacity-100 transition-opacity"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" />
                        </svg>
                      </button>
                      {lists.length > 1 && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            onDeleteList(list.id);
                          }}
                          className="opacity-60 hover:opacity-100 transition-opacity"
                        >
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                          </svg>
                        </button>
                      )}
                    </div>
                  </>
                )}
              </button>
            </div>
          ))}
        </div>
      </div>

      {activeList && (
        <div className="flex items-center space-x-4 bg-white bg-opacity-5 p-4 rounded-xl shadow-lg">
          <span className="text-gray-300">List Color:</span>
          <HexColorPicker
            color={activeList.color}
            onChange={(color) => {
              onUpdateList({ ...activeList, color });
            }}
          />
        </div>
      )}
    </div>
  );
};

export default ListSelector;
