import React, { useState } from 'react';
import { TodoList } from '../types';
import { HexColorPicker } from 'react-colorful';

interface ListSelectorProps {
  lists: TodoList[];
  activeListId: string;
  onSelectList: (id: string) => void;
  onUpdateList: (list: TodoList) => void;
  onDeleteList: (id: string) => void;
}

const ListSelector: React.FC<ListSelectorProps> = ({
  lists,
  activeListId,
  onSelectList,
  onUpdateList,
  onDeleteList,
}) => {
  const [editingListId, setEditingListId] = useState<string | null>(null);
  const [editingName, setEditingName] = useState('');
  const [showColorPicker, setShowColorPicker] = useState<string | null>(null);

  const handleEdit = (list: TodoList) => {
    setEditingListId(list.id);
    setEditingName(list.name);
  };

  const handleSave = (list: TodoList) => {
    if (editingName.trim()) {
      onUpdateList({ ...list, name: editingName.trim() });
    }
    setEditingListId(null);
  };

  return (
    <div className="flex flex-wrap justify-center gap-2">
      {lists.map((list) => (
        <div key={list.id} className="relative">
          <button
            className={`px-4 py-2 rounded-full transition-all duration-200 flex items-center space-x-2 ${
              list.id === activeListId
                ? 'bg-opacity-100'
                : 'bg-opacity-20 hover:bg-opacity-40'
            }`}
            style={{ backgroundColor: list.color }}
            onClick={() => onSelectList(list.id)}
          >
            {editingListId === list.id ? (
              <input
                type="text"
                value={editingName}
                onChange={(e) => setEditingName(e.target.value)}
                onBlur={() => handleSave(list)}
                onKeyPress={(e) => e.key === 'Enter' && handleSave(list)}
                className="bg-transparent border-none outline-none text-white placeholder-white"
                autoFocus
              />
            ) : (
              <>
                <span>{list.name}</span>
                <div className="flex space-x-1">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      setShowColorPicker(list.id);
                    }}
                    className="w-4 h-4 rounded-full border border-white"
                    style={{ backgroundColor: list.color }}
                  />
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleEdit(list);
                    }}
                    className="opacity-60 hover:opacity-100"
                  >
                    ✎
                  </button>
                  {lists.length > 1 && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteList(list.id);
                      }}
                      className="opacity-60 hover:opacity-100"
                    >
                      ×
                    </button>
                  )}
                </div>
              </>
            )}
          </button>
          {showColorPicker === list.id && (
            <div className="absolute z-10 top-full mt-2">
              <div
                className="fixed inset-0"
                onClick={() => setShowColorPicker(null)}
              />
              <HexColorPicker
                color={list.color}
                onChange={(color) => {
                  onUpdateList({ ...list, color });
                }}
              />
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default ListSelector;
